import { StatusBar } from 'expo-status-bar';
import { Alert, StyleSheet, Text, View, } from 'react-native';

export default function App() {
  return (
    <View style={styles.container}>
      <Text style={styles.text1}>Calculadora de Idade{"\n"}{"\n"}</Text>
      <Text style={styles.text2}>Digite o ano que você nasceu:{"\n"}{"\n"}</Text>
      <input style={styles.input}type="number" id="valor"/>
      <Text>{"\n"}</Text>
      <button style={styles.btn} onClick={() => Mensagem()}>Calcular</button>
      <StatusBar style="auto" />
    </View>
  );
}

function Mensagem(){
  const ano = 2023;
  const idade = document.querySelector("#valor").value;
  const idade2 = ano - idade;
  alert("Você tem "+idade2+"anos!");
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#121212',
    alignItems: 'center',
    justifyContent: 'center',
  },
  text1:{
    color: '#fff',
    fontSize: '40px'
  },
  text2:{
    color: '#fff',
    fontSize: '25px'
  },
  input:{
    textAlign: 'center',
    fontSize: '23px',
    borderRadius: 10
  },
  btn: {
    fontSize: '25px',
    borderRadius: 4,
    backgroundColor: 'grey',
    fontWeight: 'bold',
    letterSpacing: 0.25,
    color: 'white',
  },
});
